mapper.select:
select t1.article_subject_id ,t1.subject_name ,t1.create_date,t1.synopsis ,t2.author_id ,t2.username ,t2.nickname
from dts_article_subject t1
inner join uts_author t2 on t2.author_id  = t1.author_id and t1.article_subject_id = #{articleSubjectId}

select t1.article_id,t1.title,t1.create_date,t1.synopsis
from dts_article t1 , dts_article_subject_relation t3
where t3.article_id = t1.article_id  and t3.article_subject_id = #{articleSubjectId}

select t1.article_subject_id
from dts_article_subject_relation t1
where t1.article_id = #{articleId}
